import { TypedSchema, object, optional, string, typed } from '@speakeasy-api/tonic';
/**
 * payment_method_details_card_present_receipt
 *
 * 
 */
export interface PaymentMethodDetailsCardPresentReceipt {
  /**
   * The type of account being debited or credited
   */
  accountType?: string;
  /**
   * The Application Cryptogram, a unique value generated by the card to authenticate the transaction with issuers.
   */
  applicationCryptogram?: string;
  /**
   * The Application Identifier (AID) on the card used to determine which networks are eligible to process the transaction. Referenced from EMV tag 9F12, data encoded on the card's chip.
   */
  applicationPreferredName?: string;
  /**
   * Identifier for this transaction.
   */
  authorizationCode?: string;
  /**
   * EMV tag 8A. A code returned by the card issuer.
   */
  authorizationResponseCode?: string;
  /**
   * Describes the method used by the cardholder to verify ownership of the card. One of the following: `approval`, `failure`, `none`, `offline_pin`, `offline_pin_and_signature`, `online_pin`, or `signature`.
   */
  cardholderVerificationMethod?: string;
  /**
   * Similar to the application_preferred_name, identifying the applications (AIDs) available on the card. Referenced from EMV tag 84.
   */
  dedicatedFileName?: string;
  /**
   * A 5-byte string that records the checks and validations that occur between the card and the terminal. These checks determine how the terminal processes the transaction and what risk tolerance is acceptable. Referenced from EMV Tag 95.
   */
  terminalVerificationResults?: string;
  /**
   * An indication of which steps were completed during the card read process. Referenced from EMV Tag 9B.
   */
  transactionStatusInformation?: string;
}

export const PaymentMethodDetailsCardPresentReceiptSchema: TypedSchema<PaymentMethodDetailsCardPresentReceipt> = typed<PaymentMethodDetailsCardPresentReceipt>(object({
  accountType: optional(string()),
  applicationCryptogram: optional(string()),
  applicationPreferredName: optional(string()),
  authorizationCode: optional(string()),
  authorizationResponseCode: optional(string()),
  cardholderVerificationMethod: optional(string()),
  dedicatedFileName: optional(string()),
  terminalVerificationResults: optional(string()),
  transactionStatusInformation: optional(string()),
}));